    /**
     * The local storage limit has been reached.
     */
    class StorageFull(
        ) : StorageException() {
        override val message
            get() = ""
    }
    
    /**
     * Data could not be successfully synchronized across replicas.
     */
    class ReplicationException(
        ) : StorageException() {
        override val message
            get() = ""
    }
    
    /**
     * An unspecified error occurred within the storage engine.
     */
    class General(
        ) : StorageException() {
        override val message
            get() = ""
    }
    

    


    companion object ErrorHandler : UniffiRustCallStatusErrorHandler<StorageException> {
        override fun lift(error_buf: RustBuffer.ByValue): StorageException = FfiConverterTypeStorageError.lift(error_buf)
    }

    
}

/**
 * @suppress
 */
public object FfiConverterTypeStorageError : FfiConverterRustBuffer<StorageException> {
    override fun read(buf: ByteBuffer): StorageException {
        

        return when(buf.getInt()) {
            1 -> StorageException.StorageFull()
            2 -> StorageException.ReplicationException()
            3 -> StorageException.General()
            else -> throw RuntimeException("invalid error enum value, something is very wrong!!")
        }
    }

    override fun allocationSize(value: StorageException): ULong {
        return when(value) {
            is StorageException.StorageFull -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is StorageException.ReplicationException -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
            is StorageException.General -> (
                // Add the size for the Int that specifies the variant plus the size needed for all fields
                4UL
            )
        }
    }

    override fun write(value: StorageException, buf: ByteBuffer) {
        when(value) {
            is StorageException.StorageFull -> {
                buf.putInt(1)
                Unit
            }
            is StorageException.ReplicationException -> {
                buf.putInt(2)
                Unit
            }
            is StorageException.General -> {
                buf.putInt(3)
                Unit
            }
        }.let { /* this makes the `when` an expression, which ensures it is exhaustive */ }
    }

}




/**
 * @suppress
 */
public object FfiConverterOptionalString: FfiConverterRustBuffer<kotlin.String?> {
    override fun read(buf: ByteBuffer): kotlin.String? {
        if (buf.get().toInt() == 0) {
            return null
        }
        return FfiConverterString.read(buf)
    }

    override fun allocationSize(value: kotlin.String?): ULong {
        if (value == null) {
            return 1UL
        } else {
            return 1UL + FfiConverterString.allocationSize(value)
        }
    }

    override fun write(value: kotlin.String?, buf: ByteBuffer) {
        if (value == null) {
            buf.put(0)
        } else {
            buf.put(1)
            FfiConverterString.write(value, buf)
        }
    }
}




